import React from 'react';
//used to send client requests; hooks frontend code up with API
import axios from 'axios';
import { Row, Col} from 'react-bootstrap';

import './main-view.scss';


import { MovieCard } from '../movie-card/movie-card';
import { MovieView } from '../movie-view/movie-view';
import { LoginView } from '../login-view/login-view';
import { RegistrationView } from '../registration-view/registration-view';

export class MainView extends React.Component {

  constructor() {
    // Call the superclass constructor so React can initialize it
    super();

    // Initial state set to null
    this.state = {
      movies: null,
      selectedMovie: null,
      user: null,
      register: null
    };
  }

  // One of the "hooks" available in a React Component
  componentDidMount() {
    axios.get('https://starwarscentral.herokuapp.com/movies')
      .then(response => {
        // Assign the result to the state
        this.setState({
          movies: response.data
        });
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  //When a movie is clicked, this function is invoked and updates the state of the `selectedMovie` property to that movie
  onMovieClick(movie) {
    this.setState({
      selectedMovie: movie
    });
  }

  //When a user successfully logs in, this function updates the `user` property in state to that particular user
  onLoggedIn(user) {
    this.setState({
      user
    });
  }

  onRegister(register) {
    this.setState({
      register,
    });
  }

  render() {
    const { movies, selectedMovie, user, register } = this.state;

    //If there is no user, the LoginView is rendered. If there is a user logged in, the user details are passed as a prop to the LoginView
    if (!user) return <LoginView onLoggedIn={user => this.onLoggedIn(user)} />;

    //if user is not registered, register view is rendered
    if (!register)
      return (
        <RegistrationView onRegister={(register) => this.onRegister(register)} />
      );

    // Before the movies have been loaded
    if (!movies) return <div className="main-view"/>;

    return (
      <Row className="main-view justify-content-md-center">
        {selectedMovie
          ? (
              <Col md={8}>
                <MovieView movie={selectedMovie} onBackClick={movie => this.onMovieClick(null)} />
              </Col>
            )
          : movies.map(movie => (
              <Col md={3}>
                <MovieCard key={movie._id} movie={movie} onClick={movie => this.onMovieClick(movie)}/>
              </Col>
            ))
        }
      </Row>
    );
  }
}
